#!/bin/bash

curl https://nintendo.fandom.com/wiki/List_of_Super_Smash_Bros._Melee_songs -k -s | grep "<li><b>"| sed -r -e "s/<li><b>(.*)<\/li>/\1/g" -e "s/^(<ol>)?\s*(.*)<\/b> &#8211;(.*)/Title: \2 ---\3/g" -e "s/A.*<i><a.*>(.*)<\/a>(.*)<\/i>/From: \1 --- \2/g" | less

curl https://nintendo.fandom.com/wiki/List_of_Super_Smash_Bros._Melee_songs -k -s | grep "<li><b>"| sed -r -e "s/<li><b>(.*)<\/li>/\1/g" -e "s/^(<ol>)?\s*(.*)<\/b> &#8211;(.*)/Title: \2 ---\3/g" -e "s/A.*<i><(a|span).*>(.*)<\/(a|span)>(.*)<\/i>/From: \2 --- \4/g" -e "s/Plays (in)?(on)?( ?the)?(.*)/ PlaysDuring:\4/g" -e "s/(A|a)cts as an(.*)/PlaysDuring:\2/g" -e "s/<(a|span).*>(.*)<\/(a|span)>/\2/g" -e "s/From: (.*) ---.*PlaysDuring:/From: \1 --- PlaysDuring:/g" -e "s/&#160;/ /g" -e "s/:w/: W/g" -e "s/\&amp;/\&/g" -e "s/^(.*)<\/b>.*;/Title: \1 ---/g" -e "s/--- \./---/g" -e "s/---From/--- From/g" -e "s/Flat Zone (.*)--- ,.*/Flat Zone \1--- PlaysDuring: Flat Zone stage/g" -e "s/Pokémon Stadium ---.*/Pokémon Stadium ---- From: Pokémon Red, Pokémon Blue, and Pokémon Green --- PlaysDuring: Pokémon Stadium stage/g" -e "s/(Poké Floats --- From:) .* ---/\1 Pokémon Red, Blue, and Green ---/g" -e "s/(From: Earthbound ---).*/\1 PlaysDuring: Fourside stage/g" | less
